import{_ as n,c as s,o as a,ah as t}from"./chunks/framework.B9Iqqwi6.js";const c=JSON.parse('{"title":"医疗广告管理办法","description":"","frontmatter":{},"headers":[],"relativePath":"法规规章/医疗广告管理办法.md","filePath":"法规规章/医疗广告管理办法.md"}'),o={name:"法规规章/医疗广告管理办法.md"};function p(b,r,g,i,d,e){return a(),s("div",null,[...r[0]||(r[0]=[t('<h1 id="医疗广告管理办法" tabindex="-1">医疗广告管理办法 <a class="header-anchor" href="#医疗广告管理办法" aria-label="Permalink to “医疗广告管理办法”">​</a></h1><p>  <strong><span id="一">第一条</span></strong> 为加强医疗广告管理，保障人民身体健康，根据《广告法》、《医疗机构管理条例》、《中医药条例》等法律法规的规定，制定本办法。<br>   <strong><span id="二">第二条</span></strong> 本办法所称医疗广告，是指利用各种媒介或者形式直接或间接介绍医疗机构或医疗服务的广告。<br>   <strong><span id="三">第三条</span></strong> 医疗机构发布医疗广告，应当在发布前申请医疗广告审查。未取得《医疗广告审查证明》，不得发布医疗广告。<br>   <strong><span id="四">第四条</span></strong> 工商行政管理机关负责医疗广告的监督管理。<br>   卫生行政部门、中医药管理部门负责医疗广告的审查，并对医疗机构进行监督管理。<br>   <strong><span id="五">第五条</span></strong> 非医疗机构不得发布医疗广告，医疗机构不得以内部科室名义发布医疗广告。<br>   <strong><span id="六">第六条</span></strong> 医疗广告内容仅限于以下项目：<br>   （一）医疗机构第一名称；<br>   （二）医疗机构地址；<br>   （三）所有制形式；<br>   （四）医疗机构类别；<br>   （五）诊疗科目；<br>   （六）床位数；<br>   （七）接诊时间；<br>   （八）联系电话。<br>   （一）至（六）项发布的内容必须与卫生行政部门、中医药管理部门核发的《医疗机构执业许可证》或其副本载明的内容一致。<br>   <strong><span id="七">第七条</span></strong> 医疗广告的表现形式不得含有以下情形：<br>   （一）涉及医疗技术、诊疗方法、疾病名称、药物的；<br>   （二）保证治愈或者隐含保证治愈的；<br>   （三）宣传治愈率、有效率等诊疗效果的；<br>   （四）淫秽、迷信、荒诞的；<br>   （五）贬低他人的；<br>   （六）利用患者、卫生技术人员、医学教育科研机构及人员以及其他社会社团、组织的名义、形象作证明的；<br>   （七）使用解放军和武警部队名义的；<br>   （八）法律、行政法规规定禁止的其他情形。<br>   <strong><span id="八">第八条</span></strong> 医疗机构发布医疗广告，应当向其所在地省级卫生行政部门申请，并提交以下材料：<br>   （一）《医疗广告审查申请表》；<br>   （二）《医疗机构执业许可证》副本原件和复印件，复印件应当加盖核发其《医疗机构执业许可证》的卫生行政部门公章；<br>   （三）医疗广告成品样件。电视、广播广告可以先提交镜头脚本和广播文稿。<br>   中医、中西医结合、民族医医疗机构发布医疗广告，应当向其所在地省级中医药管理部门申请。<br>   <strong><span id="九">第九条</span></strong> 省级卫生行政部门、中医药管理部门应当自受理之日起20日内对医疗广告成品样件内容进行审查。卫生行政部门、中医药管理部门需要请有关专家进行审查的，可延长10日。<br>   对审查合格的医疗广告，省级卫生行政部门、中医药管理部门发给《医疗广告审查证明》，并将通过审查的医疗广告样件和核发的《医疗广告审查证明》予以公示；对审查不合格的医疗广告，应当书面通知医疗机构并告知理由。<br>   <strong><span id="十">第十条</span></strong> 省级卫生行政部门、中医药管理部门应对已审查的医疗广告成品样件和审查意见予以备案保存，保存时间自《医疗广告审查证明》生效之日起至少两年。<br>   <strong><span id="十一">第十一条</span></strong> 《医疗广告审查申请表》、《医疗广告审查证明》的格式由卫生部、国家中医药管理局规定。<br>   <strong><span id="十二">第十二条</span></strong> 省级卫生行政部门、中医药管理部门应在核发《医疗广告审查证明》之日起五个工作日内，将《医疗广告审查证明》抄送本地同级工商行政管理机关。<br>   <strong><span id="十三">第十三条</span></strong> 《医疗广告审查证明》的有效期为一年。到期后仍需继续发布医疗广告的，应重新提出审查申请。<br>   <strong><span id="十四">第十四条</span></strong> 发布医疗广告应当标注医疗机构第一名称和《医疗广告审查证明》文号。<br>   <strong><span id="十五">第十五条</span></strong> 医疗机构发布户外医疗广告，应在取得《医疗广告审查证明》后，按照《户外广告登记管理规定》办理登记。<br>   医疗机构在其法定控制地带标示仅含有医疗机构名称的户外广告，无需申请医疗广告审查和户外广告登记。<br>   <strong><span id="十六">第十六条</span></strong> 禁止利用新闻形式、医疗资讯服务类专题节（栏）目发布或变相发布医疗广告。<br>   有关医疗机构的人物专访、专题报道等宣传内容，可以出现医疗机构名称，但不得出现有关医疗机构的地址、联系方式等医疗广告内容；不得在同一媒介的同一时间段或者版面发布该医疗机构的广告。<br>   <strong><span id="十七">第十七条</span></strong> 医疗机构应当按照《医疗广告审查证明》核准的广告成品样件内容与媒体类别发布医疗广告。<br>   医疗广告内容需要改动或者医疗机构的执业情况发生变化，与经审查的医疗广告成品样件内容不符的，医疗机构应当重新提出审查申请。<br>   <strong><span id="十八">第十八条</span></strong> 广告经营者、广告发布者发布医疗广告，应当由其广告审查员查验《医疗广告审查证明》，核实广告内容。<br>   <strong><span id="十九">第十九条</span></strong> 有下列情况之一的，省级卫生行政部门、中医药管理部门应当收回《医疗广告审查证明》，并告知有关医疗机构：<br>   （一）医疗机构受到停业整顿、吊销《医疗机构执业许可证》的；<br>   （二）医疗机构停业、歇业或被注销的；<br>   （三）其他应当收回《医疗广告审查证明》的情形。<br>   <strong><span id="二十">第二十条</span></strong> 医疗机构违反本办法规定发布医疗广告，县级以上地方卫生行政部门、中医药管理部门应责令其限期改正，给予警告；情节严重的，核发《医疗机构执业许可证》的卫生行政部门、中医药管理部门可以责令其停业整顿、吊销有关诊疗科目，直至吊销《医疗机构执业许可证》。<br>   未取得《医疗机构执业许可证》发布医疗广告的，按非法行医处罚。<br>   <strong><span id="二十一">第二十一条</span></strong> 医疗机构篡改《医疗广告审查证明》内容发布医疗广告的，省级卫生行政部门、中医药管理部门应当撤销《医疗广告审查证明》，并在一年内不受理该医疗机构的广告审查申请。<br>   省级卫生行政部门、中医药管理部门撤销《医疗广告审查证明》后，应当自作出行政处理决定之日起5个工作日内通知同级工商行政管理机关，工商行政管理机关应当依法予以查处。<br>   <strong><span id="二十二">第二十二条</span></strong> 工商行政管理机关对违反本办法规定的广告主、广告经营者、广告发布者依据《广告法》、《反不正当竞争法》予以处罚，对情节严重，造成严重后果的，可以并处一至六个月暂停发布医疗广告、直至取消广告经营者、广告发布者的医疗广告经营和发布资格的处罚。法律法规没有规定的，工商行政管理机关应当对负有责任的广告主、广告经营者、广告发布者给予警告或者处以一万元以上三万元以下的罚款；医疗广告内容涉嫌虚假的，工商行政管理机关可根据需要会同卫生行政部门、中医药管理部门作出认定。<br>   <strong><span id="二十三">第二十三条</span></strong> 本办法自2007年1月1日起施行。<br>   </p>',2)])])}const l=n(o,[["render",p]]);export{c as __pageData,l as default};
